:root {
    --font-family-sans-serif: "Helvetica Neue", Helvetica, Arial, sans-serif;
    --font-family-serif: Georgia, "Times New Roman", Times, serif;
}

h1,
.heading {
    text-rendering: optimizeLegibility;
    font-size: 2rem;
    font-weight: normal;
    font-feature-settings: "kern" 1;
    font-kerning: normal;
    line-height: 1.2;
}

a,
.link {
    cursor: pointer;
    text-decoration: none;
    color: inherit;
}

/* Block-level */

.link.-block {
    display: block;
    width: 100%;
}

/* States */

a:hover,
a:focus,
a:active,
.link:hover,
.link:focus,
.link:active {
    opacity: .8;
}

.icon {
    display: inline-block;
    width: 1rem;
    height: 1rem;
    vertical-align: middle;
    fill: currentColor;
}

/* Colors */

.icon.-green {
    fill: var(--brand-green);
}

/* Sizes */

.icon.-sm {
    width: .8rem;
    height: .8rem;
}

.icon.-lg {
    width: 1.2rem;
    height: 1.2rem;
}

.icon.-xl {
    width: 1.4rem;
    height: 1.4rem;
}

.loading {
    display: inline-block;
    width: 1rem;
    height: 1rem;
    animation: loading linear 1s infinite;
    border: 1px solid currentColor;
    border-top-color: transparent !important;
    border-right-color: transparent !important;
    border-radius: 50%;
}

@keyframes loading {
    0%   { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

/* Colors */

.loading.-green {
    border-color: var(--brand-green);
}

/* Sizes */

.loading.-sm {
    width: .8rem;
    height: .8rem;
}

.loading.-lg {
    width: 1.2rem;
    height: 1.2rem;
    border-width: 2px;
}

.loading.-xl {
    width: 1.4rem;
    height: 1.4rem;
    border-width: 2px;
}

.button {
    position: relative;
    display: inline-block;
    padding: .65em 1em;
    user-select: none;
    text-align: center;
    vertical-align: middle;
    color: var(--gray-darker);
    border: 1px solid var(--gray-light);
    border-radius: 3px;
    background: var(--white);
    font-size: 1rem;
    line-height: 1;
}

/* Colors */

.button.-green {
    color: var(--white);
    border: none;
    background: var(--brand-green);
}

/* Sizes */

.button.-sm {
    font-size: .8rem;
}

.button.-lg {
    font-size: 1.2rem;
}

.button.-xl {
    font-size: 1.4rem;
}

/* Block-level */

.button.-block {
    display: block;
    width: 100%;
    padding-right: 0;
    padding-left: 0;
}

/* States */

.button:hover,
.button:focus,
.button:active {
    text-decoration: none;
    opacity: .8;
}

.button:disabled,
.button.is-disabled {
    cursor: default;
    pointer-events: none;
    opacity: .6;
}

/**
 * Button text
 */

.button__text {
    display: inline;
    vertical-align: top;
}

.button__text.-middle {
    vertical-align: middle;
}

.button__text.-bottom {
    vertical-align: bottom;
}

.input {
    display: block;
    width: 12rem;
    max-width: 100%;
    height: 34px;
    padding: .6em .8em;
    border: 1px solid var(--gray-light);
    border-radius: 3px;
    background: var(--white);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .06);
    font-size: 1rem;
}

/* Sizes */

.input.-xs {
    width: 3rem;
}

.input.-sm {
    width: 7.5rem;
}

.input.-lg {
    width: 16.5rem;
}

.input.-xl {
    width: 21rem;
}

/* Block-level */

.input.-block {
    width: 100%;
}

/* Inline-level */

.input.-inline {
    display: inline-block;
    vertical-align: top;
}

/* Select */

.input.-select {
    position: relative;
    padding: 0;
}

.input.-select > select {
    display: block;
    width: 100%;
    height: 100%;
    padding: .6em .8em;
    border: none;
    background: transparent;
    line-height: 1;
    -webkit-appearance: none;
       -moz-appearance: none;
            appearance: none;
}

.input.-select > select::-ms-expand {
    display: none;
}

.input.-select::before,
.input.-select::after {
    position: absolute;
    top: .75em;
    right: .8em;
    height: 0;
    content: "";
    border-width: 4px;
    border-style: dashed;
    border-color: var(--gray-dark) transparent;
}

.input.-select::before {
    border-top: none;
    border-bottom-style: solid;
}

.input.-select::after {
    margin-top: 7px;
    border-top-style: solid;
    border-bottom: none;
}

/* Textarea */

.input.-textarea {
    min-height: 8em;
    resize: vertical;
}

/* States */

.input:disabled,
.input.is-disabled {
    cursor: default;
    pointer-events: none;
    opacity: .6;
}

.list {
    font-size: 1rem;
}

/* Unordered list */

.list.-unordered > li::before {
    margin-right: .5em;
    content: "•";
    color: var(--gray);
}

/* Ordered list */

.list.-ordered {
    counter-reset: ordered-list-counter;
}

.list.-ordered > li::before {
    margin-right: .5em;
    content: counter(ordered-list-counter)".";
    counter-increment: ordered-list-counter;
    color: var(--gray);
}

/* Inline-level */

.list.-inline {
    margin-left: -20px;
}

.list.-inline > li {
    display: inline-block;
    padding-left: 20px;
}

table {
    width: 100%;
    max-width: 100%;
}

th {
    text-align: left;
}

.grid {
    display: flex;
    flex-wrap: wrap;
}

/* With gutter */

.grid.-gutter {
    margin-top: -20px;
    margin-left: -20px;
}

.grid.-gutter > .grid__cell {
    padding-top: 20px;
    padding-left: 20px;
}

/* Justify per row */

.grid.-center {
    justify-content: center;
}

.grid.-right {
    justify-content: flex-end;
}

/* Alignment per row */

.grid.-middle {
    align-items: center;
}

.grid.-bottom {
    align-items: flex-end;
}

/* Fill the height */

.grid.-stretch > .grid__cell {
    display: flex;
}

.grid__cell {
    flex: 1;
    min-width: 1px; /* for multi-lines */
}

/* Intrinsic widths */

.grid__cell.-fit {
    flex: none;
}

.grid__cell.-full {
    flex: 0 0 100%;
}

/* Proportional widths */

.grid__cell.\-1of12 {
    flex: 0 0 calc(100% * 1 / 12);
    width: calc(100% * 1 / 12); /* for multi-lines of IE */
}

.grid__cell.\-2of12 {
    flex: 0 0 calc(100% * 2 / 12);
    width: calc(100% * 2 / 12);
}

.grid__cell.\-3of12 {
    flex: 0 0 calc(100% * 3 / 12);
    width: calc(100% * 3 / 12);
}

.grid__cell.\-4of12 {
    flex: 0 0 calc(100% * 4 / 12);
    width: calc(100% * 4 / 12);
}

.grid__cell.\-5of12 {
    flex: 0 0 calc(100% * 5 / 12);
    width: calc(100% * 5 / 12);
}

.grid__cell.\-6of12 {
    flex: 0 0 calc(100% * 6 / 12);
    width: calc(100% * 6 / 12);
}

.grid__cell.\-7of12 {
    flex: 0 0 calc(100% * 7 / 12);
    width: calc(100% * 7 / 12);
}

.grid__cell.\-8of12 {
    flex: 0 0 calc(100% * 8 / 12);
    width: calc(100% * 8 / 12);
}

.grid__cell.\-9of12 {
    flex: 0 0 calc(100% * 9 / 12);
    width: calc(100% * 9 / 12);
}

.grid__cell.\-10of12 {
    flex: 0 0 calc(100% * 10 / 12);
    width: calc(100% * 10 / 12);
}

.grid__cell.\-11of12 {
    flex: 0 0 calc(100% * 11 / 12);
    width: calc(100% * 11 / 12);
}

/* Reorder */

.grid__cell.-first {
    order: -1;
}

.grid__cell.-last {
    order: 1;
}
